import { useState } from "react";

import "./App.css";

interface Note {
  id: number;
  title: string;
  content: string;
}

function App() {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [notes, setNotes] = useState<Note[]>([]);

  const handleAddNote = (event: React.FormEvent) => {
    event.preventDefault();
    setNotes([newNote, ...notes]);
    setTitle("");
    setContent("");
  };

  const newNote: Note = {
    id: notes.length + 1,
    title: title,
    content: content,
  };

  return (
    <div className="app-container">
      <form className="note-form" onSubmit={handleAddNote}>
        <input
          placeholder="Title"
          value={title}
          onChange={(e) => {
            setTitle(e.target.value);
          }}
          required
        />
        <textarea
          value={content}
          rows={10}
          onChange={(e) => {
            setContent(e.target.value);
          }}
          placeholder="Content"
          required
        ></textarea>
        <button type="submit">Add Note</button>
      </form>
      <div className="notes-grid">
        {notes.map((note) => (
          <div key={note.title + note.id} className="note-item">
            <div className="notes-header">
              <button>x</button>
            </div>
            <h2>{note.title}</h2>
            <p>{note.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
